import React, { useState } from "react";
import {
  FaBalanceScaleLeft,
  FaBalanceScaleRight,
  FaChartLine,
  FaBoxes,
  FaShoppingCart,
  FaExchangeAlt,
  FaSignOutAlt,
} from "react-icons/fa";

const DashboardCard = ({
  title,
  icon: Icon,
  value,
  clickable,
  onClick,
  progress,
  selected,
}) => {
  const progressStyle = progress ? { "--progress": progress } : {};

  return (
    <div
      className={`card ${clickable ? "clickable" : ""} ${
        selected ? "selected" : ""
      }`}
      onClick={clickable ? onClick : undefined}
      role={clickable ? "button" : undefined}
      tabIndex={clickable ? 0 : undefined}
      onKeyDown={clickable ? (e) => e.key === "Enter" && onClick() : undefined}
      aria-pressed={selected ? "true" : "false"}
      aria-label={title}
    >
      <div className="card-header">
        <Icon className="icon" />
        <h3>{title}</h3>
      </div>
      <p className="value">{value}</p>
      {progress && (
        <div className="progress-bar">
          <div className="progress-fill" style={progressStyle}></div>
        </div>
      )}
    </div>
  );
};

const Filter = ({ label, options, value, onChange }) => (
  <select value={value} onChange={(e) => onChange(e.target.value)} aria-label={label}>
    <option value="">{label}</option>
    {options.map((opt) => (
      <option key={opt} value={opt}>
        {opt}
      </option>
    ))}
  </select>
);

export default function Dashboard() {
  const [dateFilter, setDateFilter] = useState("");
  const [baseFilter, setBaseFilter] = useState("");
  const [equipFilter, setEquipFilter] = useState("");
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);

  const openingBalance = 125000;
  const closingBalance = 143500;
  const purchases = 15000;
  const transferIn = 8000;
  const transferOut = 12000;
  const netMovement = purchases + transferIn - transferOut;
  const assignedAssets = 320;
  const expendedAssets = 45;
  const warehouseStock = 1000;
  const ordersPending = 75;
  const returns = 12;

  const dateOptions = ["Jan 2025", "Feb 2025", "Mar 2025", "Apr 2025"];
  const baseOptions = ["Base A", "Base B", "Base C"];
  const equipOptions = ["Type 1", "Type 2", "Type 3"];

  const cards = [
    {
      key: "opening",
      title: "Opening Balance",
      icon: FaBalanceScaleLeft,
      value: `$${openingBalance.toLocaleString()}`,
      progress: "70%",
      clickable: false,
    },
    {
      key: "closing",
      title: "Closing Balance",
      icon: FaBalanceScaleRight,
      value: `$${closingBalance.toLocaleString()}`,
      progress: "85%",
      clickable: false,
    },
    {
      key: "netMovement",
      title: "Net Movement",
      icon: FaChartLine,
      value: `$${netMovement.toLocaleString()}`,
      progress: "65%",
      clickable: true,
      onClick: () => {
        setSelectedCard("netMovement");
        setModalOpen(true);
      },
    },
    {
      key: "assignedAssets",
      title: "Assigned Assets",
      icon: FaBoxes,
      value: assignedAssets,
      progress: "90%",
      clickable: false,
    },
    {
      key: "expendedAssets",
      title: "Expended Assets",
      icon: FaBoxes,
      value: expendedAssets,
      progress: "45%",
      clickable: false,
    },
    {
      key: "warehouseStock",
      title: "Purchase",
      icon: FaBoxes,
      value: warehouseStock,
      progress: "80%",
      clickable: false,
    },
    {
      key: "ordersPending",
      title: "Target",
      icon: FaShoppingCart,
      value: ordersPending,
      progress: "30%",
      clickable: true,
      onClick: () => {
        setSelectedCard("ordersPending");
        setModalOpen(true);
      },
    },
    {
      key: "returns",
      title: "Returns",
      icon: FaExchangeAlt,
      value: returns,
      progress: "20%",
      clickable: true,
      onClick: () => {
        setSelectedCard("returns");
        setModalOpen(true);
      },
    },
    {
      key: "logout",
      title: "Logout",
      icon: FaSignOutAlt,
      value: "-",
      clickable: false,
    },
  ];

  const handleCardClick = (key, onClick) => {
    setSelectedCard(key);
    if (onClick) onClick();
  };

  const renderModalContent = () => {
    if (selectedCard === "netMovement") {
      return (
        <ul>
          <li>
            <FaShoppingCart color="#3b82f6" /> Purchases: ${purchases.toLocaleString()}
          </li>
          <li>
            <FaExchangeAlt color="#8b5cf6" /> Transfer In: ${transferIn.toLocaleString()}
          </li>
          <li>
            <FaSignOutAlt color="#ec4899" /> Transfer Out: ${transferOut.toLocaleString()}
          </li>
        </ul>
      );
    }
    if (selectedCard === "ordersPending") {
      return (
        <p>There are currently {ordersPending} orders pending for processing.</p>
      );
    }
    if (selectedCard === "returns") {
      return <p>Total returns recorded: {returns}</p>;
    }
    return <p>No details available.</p>;
  };

  return (
    <div className="App" role="main">
      <h1 className="title">ðŸŒŸ Military Asset Management Dashboard</h1>

      <div className="filters" role="region" aria-label="Filters">
        <Filter label="Select Date" options={dateOptions} value={dateFilter} onChange={setDateFilter} />
        <Filter label="Select Base" options={baseOptions} value={baseFilter} onChange={setBaseFilter} />
        <Filter label="Equipment Type" options={equipOptions} value={equipFilter} onChange={setEquipFilter} />
      </div>

      <div className="dashboard" aria-label="Dashboard Key Metrics">
        {cards.map(({ key, title, icon, value, clickable, onClick, progress }) => (
          <DashboardCard
            key={key}
            title={title}
            icon={icon}
            value={value}
            clickable={clickable}
            onClick={() => handleCardClick(key, onClick)}
            progress={progress}
            selected={selectedCard === key}
          />
        ))}
      </div>

      {modalOpen && (
        <div
          className="modal-overlay"
          role="dialog"
          aria-modal="true"
          aria-labelledby="modalTitle"
          onClick={() => {
            setModalOpen(false);
            setSelectedCard(null);
          }}
        >
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2 id="modalTitle">{cards.find((c) => c.key === selectedCard)?.title} Details</h2>
            {renderModalContent()}
            <button
              onClick={() => {
                setModalOpen(false);
                setSelectedCard(null);
              }}
              aria-label="Close Modal"
              className="close-btn"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
