import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
    FaBalanceScaleLeft,
    FaBalanceScaleRight,
    FaChartLine,
    FaBoxes,
    FaShoppingCart,
    FaExchangeAlt,
    FaSignOutAlt,
} from "react-icons/fa";
import "./Dashboard.css"; // Ensure styling is handled properly

const DashboardCard = ({
    title,
    icon: Icon,
    value,
    clickable,
    onClick,
    progress,
    selected,
}) => {
    const progressStyle = progress ? { width: progress } : {};

    return (
        <div
            className={`card ${clickable ? "clickable" : ""} ${selected ? "selected" : ""}`}
            onClick={clickable ? onClick : undefined}
            role={clickable ? "button" : undefined}
            tabIndex={clickable ? 0 : undefined}
            onKeyDown={clickable ? (e) => e.key === "Enter" && onClick() : undefined}
            aria-pressed={selected ? "true" : "false"}
            aria-label={title}
        >
            <div className="card-header">
                <Icon className="icon" />
                <h3>{title}</h3>
            </div>
            <p className="value">{value}</p>
            {progress && (
                <div className="progress-bar">
                    <div className="progress-fill" style={progressStyle}></div>
                </div>
            )}
        </div>
    );
};

const Filter = ({ id, label, options, value, onChange }) => (
    <div className="filter-group">
        <label htmlFor={id} className="filter-label">{label}</label>
        <select id={id} value={value} onChange={(e) => onChange(e.target.value)} aria-label={label}>
            <option value="">{label}</option>
            {options.map((opt) => (
                <option key={opt} value={opt}>{opt}</option>
            ))}
        </select>
    </div>
);

export default function Dashboard() {
    const navigate = useNavigate();

    const [dateFilter, setDateFilter] = useState("");
    const [baseFilter, setBaseFilter] = useState("");
    const [equipFilter, setEquipFilter] = useState("");
    const [selectedCard, setSelectedCard] = useState(null);
    const [showPopup, setShowPopup] = useState(false);

    // Dummy data values
    const openingBalance = 125000;
    const closingBalance = 143500;
    const purchases = 15000;
    const transferIn = 8000;
    const transferOut = 12000;
    const netMovement = purchases + transferIn - transferOut;
    const assignedAssets = 320;
    const expendedAssets = 45;
    const warehouseStock = 1000;
    const ordersPending = 75;
    const returns = 12;

    const dateOptions = ["Jan 2025", "Feb 2025", "Mar 2025", "Apr 2025"];
    const baseOptions = ["Base A", "Base B", "Base C"];
    const equipOptions = ["Type 1", "Type 2", "Type 3"];

    const handleCardClick = (key, onClick) => {
        setSelectedCard(key);
        if (key === "netMovement") {
            setShowPopup(true);
        } else {
            setShowPopup(false);
            if (onClick) onClick();
        }
    };

    const cards = [
        { key: "opening", title: "Opening Balance", icon: FaBalanceScaleLeft, value: `$${openingBalance.toLocaleString()}`, progress: "70%" },
        { key: "closing", title: "Closing Balance", icon: FaBalanceScaleRight, value: `$${closingBalance.toLocaleString()}`, progress: "85%" },
        { key: "netMovement", title: "Net Movement", icon: FaChartLine, value: `$${netMovement.toLocaleString()}`, progress: "65%", clickable: true },
        { key: "assignedAssets", title: "Assigned Assets", icon: FaBoxes, value: assignedAssets, progress: "90%" },
        { key: "expendedAssets", title: "Expended Assets", icon: FaBoxes, value: expendedAssets, progress: "45%", clickable: true, onClick: () => navigate("/expenditure") },
        { key: "warehouseStock", title: "Purchase", icon: FaBoxes, value: warehouseStock, progress: "80%", clickable: true, onClick: () => navigate("/purchases") },
        { key: "ordersPending", title: "Target", icon: FaShoppingCart, value: ordersPending, progress: "30%", clickable: true, onClick: () => navigate("/transfers") },
        { key: "returns", title: "Returns", icon: FaExchangeAlt, value: returns, progress: "20%", clickable: true, onClick: () => navigate("/assignments") },
        { key: "logout", title: "Logout", icon: FaSignOutAlt, value: "-" },
    ];

    return (
        <div className="App" role="main">
            <h1 className="title">ðŸŒŸ Military Asset Management Dashboard</h1>

            <div className="filters" role="region" aria-label="Filters">
                <Filter id="date-filter" label="Date Label" options={dateOptions} value={dateFilter} onChange={setDateFilter} />
                <Filter id="base-filter" label="Base Label" options={baseOptions} value={baseFilter} onChange={setBaseFilter} />
                <Filter id="equip-filter" label="Equipment Type" options={equipOptions} value={equipFilter} onChange={setEquipFilter} />
            </div>

            <div className="dashboard" role="list">
                {cards.map(({ key, title, icon, value, clickable, onClick, progress }) => (
                    <DashboardCard
                        key={key}
                        title={title}
                        icon={icon}
                        value={value}
                        clickable={clickable}
                        onClick={() => handleCardClick(key, onClick)}
                        progress={progress}
                        selected={selectedCard === key}
                    />
                ))}
            </div>

            {showPopup && (
                <div className="popup-overlay" onClick={() => setShowPopup(false)}>
                    <div className="popup" onClick={(e) => e.stopPropagation()}>
                        <h2>ðŸ“Š Net Movement Details</h2>
                        <ul>
                            <li><strong>Purchases:</strong> ${purchases.toLocaleString()}</li>
                            <li><strong>Transfer In:</strong> ${transferIn.toLocaleString()}</li>
                            <li><strong>Transfer Out:</strong> ${transferOut.toLocaleString()}</li>
                        </ul>
                        <button onClick={() => setShowPopup(false)}>Close</button>
                    </div>
                </div>
            )}
        </div>
    );
}
