import React, { useState } from "react";
import "./PurchasesPage.css";

const baseOptions = ["Base A", "Base B", "Base C"];
const equipmentOptions = ["Weapons", "Vehicles", "Other"];

export default function PurchasesPage() {
  const [purchases, setPurchases] = useState([]);
  const [formData, setFormData] = useState({
    base: "",
    equipmentType: "",
    assetName: "",
    quantity: 1,
    purchaseDate: "",
  });

  const [filters, setFilters] = useState({
    date: "",
    equipmentType: "",
  });

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle filter changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  // Submit new purchase record
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.base || !formData.equipmentType || !formData.assetName || !formData.purchaseDate) {
      alert("Please fill all required fields.");
      return;
    }
    setPurchases((prev) => [...prev, { ...formData, id: Date.now() }]);
    setFormData({
      base: "",
      equipmentType: "",
      assetName: "",
      quantity: 1,
      purchaseDate: "",
    });
  };

  // Filter purchases based on filters state
  const filteredPurchases = purchases.filter((p) => {
    const matchesDate = filters.date ? p.purchaseDate === filters.date : true;
    const matchesEquip = filters.equipmentType ? p.equipmentType === filters.equipmentType : true;
    return matchesDate && matchesEquip;
  });

  return (
    <div className="purchases-page" role="main" aria-label="Purchases Page">
      <h1>ðŸ“¦ Purchases for Assets</h1>

      {/* Form to record new purchase */}
      <form className="purchase-form" onSubmit={handleSubmit} aria-label="Record Purchase Form">
        <fieldset>
          <legend>Record New Purchase</legend>

          <label htmlFor="base">Select Base</label>
          <select
            id="base"
            name="base"
            value={formData.base}
            onChange={handleChange}
            required
          >
            <option value="">-- Select Base --</option>
            {baseOptions.map((base) => (
              <option key={base} value={base}>
                {base}
              </option>
            ))}
          </select>

          <label htmlFor="equipmentType">Equipment Type</label>
          <select
            id="equipmentType"
            name="equipmentType"
            value={formData.equipmentType}
            onChange={handleChange}
            required
          >
            <option value="">-- Select Equipment Type --</option>
            {equipmentOptions.map((equip) => (
              <option key={equip} value={equip}>
                {equip}
              </option>
            ))}
          </select>

          <label htmlFor="assetName">Asset Name</label>
          <input
            type="text"
            id="assetName"
            name="assetName"
            value={formData.assetName}
            onChange={handleChange}
            placeholder="e.g., M16 Rifle"
            required
          />

          <label htmlFor="quantity">Quantity</label>
          <input
            type="number"
            id="quantity"
            name="quantity"
            min="1"
            value={formData.quantity}
            onChange={handleChange}
            required
          />

          <label htmlFor="purchaseDate">Purchase Date</label>
          <input
            type="date"
            id="purchaseDate"
            name="purchaseDate"
            value={formData.purchaseDate}
            onChange={handleChange}
            required
          />

          <button type="submit" className="btn-primary" aria-label="Add Purchase">
            Add Purchase
          </button>
        </fieldset>
      </form>

      {/* Filters */}
      <section className="filters" aria-label="Filter Purchases">
        <h2>Filter Historical Purchases</h2>
        <label htmlFor="filterDate">Filter by Date</label>
        <input
          type="date"
          id="filterDate"
          name="date"
          value={filters.date}
          onChange={handleFilterChange}
        />

        <label htmlFor="filterEquip">Filter by Equipment Type</label>
        <select
          id="filterEquip"
          name="equipmentType"
          value={filters.equipmentType}
          onChange={handleFilterChange}
        >
          <option value="">-- All Types --</option>
          {equipmentOptions.map((equip) => (
            <option key={equip} value={equip}>
              {equip}
            </option>
          ))}
        </select>
      </section>

      {/* Purchases List */}
      <section className="purchases-list" aria-label="List of Purchases">
        <h2>Historical Purchases</h2>
        {filteredPurchases.length === 0 ? (
          <p>No purchases found.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Base</th>
                <th>Equipment Type</th>
                <th>Asset Name</th>
                <th>Quantity</th>
                <th>Purchase Date</th>
              </tr>
            </thead>
            <tbody>
              {filteredPurchases.map(({ id, base, equipmentType, assetName, quantity, purchaseDate }) => (
                <tr key={id}>
                  <td>{base}</td>
                  <td>{equipmentType}</td>
                  <td>{assetName}</td>
                  <td>{quantity}</td>
                  <td>{purchaseDate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
}
