// ExpenditurePage.js
import React, { useState } from 'react';
import './ExpenditurePage.css'; // Correct CSS import path for this component

// Dummy data for assets (in a real application, this would come from an API or global state)
const dummyAssets = [
    { id: 'AST001', name: 'M16 Rifle', status: 'Available' },
    { id: 'AST002', name: 'Humvee', status: 'Available' },
    { id: 'AST003', name: 'Night Vision Goggles', status: 'Available' },
    { id: 'AST004', name: 'First Aid Kit', status: 'Available' },
    // Add more dummy assets or fetch from a service
];

export default function ExpenditurePage() { // Component name changed to ExpenditurePage
    const [expendedAssets, setExpendedAssets] = useState([]); // State to hold the list of expended assets

    // State for the expenditure form inputs
    const [expenditureForm, setExpenditureForm] = useState({
        expendedAssetId: '',
        expenditureDate: '',
        reason: '',
    });

    // Handles changes to the expenditure form inputs
    const handleExpenditureChange = (e) => {
        const { name, value } = e.target;
        setExpenditureForm(prev => ({ ...prev, [name]: value }));
    };

    // Handles the submission of the expenditure form
    const handleExpendedAsset = (e) => {
        e.preventDefault(); // Prevent default form submission behavior
        const { expendedAssetId, expenditureDate, reason } = expenditureForm;

        // Basic validation
        if (!expendedAssetId || !expenditureDate || !reason) {
            alert('Please fill all expenditure fields.');
            return;
        }

        // Find the asset to be marked as expended from dummy data
        const assetToExpend = dummyAssets.find(a => a.id === expendedAssetId);
        if (!assetToExpend) {
            alert('Asset not found. Please check the Asset ID.');
            return;
        }

        // In a real app, you'd update the asset's status in your backend
        // For this dummy example, we'll just check the dummy status.
        if (assetToExpend.status === 'Expended') {
            alert('Asset is already marked as expended.');
            return;
        }

        const newExpenditure = {
            id: Date.now(), // Unique ID for the expenditure record
            asset: { id: assetToExpend.id, name: assetToExpend.name },
            expenditureDate,
            reason,
        };

        // Add the new expenditure to the list
        setExpendedAssets(prev => [...prev, newExpenditure]);
        // Clear the form fields
        setExpenditureForm({ expendedAssetId: '', expenditureDate: '', reason: '' });
        alert('Asset marked as expended successfully!');
    };

    return (
        <div className="expenditure-page"> {/* Class name changed to expenditure-page */}
            <h1 className="page-title">
                <span role="img" aria-label="bin">üóëÔ∏è</span> Asset Expenditures
            </h1>

            <section className="expenditure-section">
                <h2 className="section-title">
                    <span role="img" aria-label="bin">üóëÔ∏è</span> Mark Asset as Expended
                </h2>
                <form onSubmit={handleExpendedAsset} className="asset-form">
                    <div className="form-grid">
                        <div className="form-group">
                            <label htmlFor="expendedAssetId">Asset to Expend:</label>
                            <select
                                id="expendedAssetId"
                                name="expendedAssetId"
                                value={expenditureForm.expendedAssetId}
                                onChange={handleExpenditureChange}
                                required
                            >
                                <option value="">-- Select Asset --</option>
                                {dummyAssets.map(asset => (
                                    <option key={asset.id} value={asset.id}>
                                        {asset.name} ({asset.id}) - {asset.status}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label htmlFor="expenditureDate">Expenditure Date:</label>
                            <input
                                type="date"
                                id="expenditureDate"
                                name="expenditureDate"
                                value={expenditureForm.expenditureDate}
                                onChange={handleExpenditureChange}
                                required
                            />
                        </div>
                        <div className="form-group full-width">
                            <label htmlFor="reason">Reason for Expenditure:</label>
                            <textarea
                                id="reason"
                                name="reason"
                                value={expenditureForm.reason}
                                onChange={handleExpenditureChange}
                                placeholder="e.g., Damaged beyond repair, Lost in action, Consumed"
                                rows="3"
                                required
                            ></textarea>
                        </div>
                    </div>
                    <button type="submit" className="submit-button delete-button">Mark Expended</button>
                </form>

                <h3 className="sub-section-title">Expended Assets History</h3>
                {expendedAssets.length === 0 ? (
                    <p className="empty-state">No assets have been marked as expended.</p>
                ) : (
                    <div className="expenditures-table-container">
                        <table className="data-table">
                            <thead>
                                <tr>
                                    <th>Asset ID</th>
                                    <th>Asset Name</th>
                                    <th>Expenditure Date</th>
                                    <th>Reason</th>
                                </tr>
                            </thead>
                            <tbody>
                                {expendedAssets.map(expenditure => (
                                    <tr key={expenditure.id}>
                                        <td>{expenditure.asset.id}</td>
                                        <td>{expenditure.asset.name}</td>
                                        <td>{expenditure.expenditureDate}</td>
                                        <td>{expenditure.reason}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </section>
        </div>
    );
}
