// frontend/src/ExpenditurePage.js
import React, { useState, useEffect } from 'react';
import './ExpenditurePage.css'; // Correct CSS import path for this component
import axios from 'axios'; // For making HTTP requests

export default function ExpenditurePage() {
    const [availableAssets, setAvailableAssets] = useState([]); // Assets for the dropdown
    const [expendedAssetsHistory, setExpendedAssetsHistory] = useState([]); // History of expenditures

    // State for the expenditure form inputs
    const [expenditureForm, setExpenditureForm] = useState({
        expendedAssetId: '', // This will be the custom 'id' (e.g., AST001)
        expenditureDate: '',
        reason: '',
    });

    const API_BASE_URL = 'http://localhost:5000/api/expenditures'; // Adjust if your backend port or route prefix is different
    const ASSET_API_URL = 'http://localhost:5000/api/assignments/assets'; // Existing route to get all assets

    // Fetch available assets for the dropdown and existing expenditure history on component mount
    useEffect(() => {
        fetchAvailableAssets();
        fetchExpendedAssetsHistory();
    }, []);

    const fetchAvailableAssets = async () => {
        try {
            // Fetch assets that are not yet retired (status 'Available' or 'Assigned')
            const response = await axios.get(`${API_BASE_URL}/available-assets`);
            setAvailableAssets(response.data);
        } catch (error) {
            console.error('Error fetching available assets:', error);
            alert('Failed to fetch available assets. Please check console for details.');
        }
    };

    const fetchExpendedAssetsHistory = async () => {
        try {
            const response = await axios.get(API_BASE_URL);
            setExpendedAssetsHistory(response.data);
        } catch (error) {
            console.error('Error fetching expended assets history:', error);
            alert('Failed to fetch expended assets history. Please check console for details.');
        }
    };

    // Handles changes to the expenditure form inputs
    const handleExpenditureChange = (e) => {
        const { name, value } = e.target;
        setExpenditureForm(prev => ({ ...prev, [name]: value }));
    };

    // Handles the submission of the expenditure form
    const handleExpendedAsset = async (e) => {
        e.preventDefault();
        const { expendedAssetId, expenditureDate, reason } = expenditureForm;

        if (!expendedAssetId || !expenditureDate || !reason) {
            alert('Please fill all expenditure fields.');
            return;
        }

        try {
            const response = await axios.post(API_BASE_URL, {
                assetId: expendedAssetId, // Send the custom asset ID
                expenditureDate,
                reason,
            });

            alert(response.data.message);
            // Refresh both lists after successful expenditure
            fetchAvailableAssets();
            fetchExpendedAssetsHistory();
            // Clear the form fields
            setExpenditureForm({ expendedAssetId: '', expenditureDate: '', reason: '' });

        } catch (error) {
            console.error('Error marking asset as expended:', error.response ? error.response.data : error.message);
            alert(error.response?.data?.message || 'Failed to mark asset as expended.');
        }
    };

    return (
        <div className="expenditure-page">
            <h1 className="page-title">
                <span role="img" aria-label="bin">üóëÔ∏è</span> Asset Expenditures
            </h1>

            <section className="expenditure-section">
                <h2 className="section-title">
                    <span role="img" aria-label="bin">üóëÔ∏è</span> Mark Asset as Expended
                </h2>
                <form onSubmit={handleExpendedAsset} className="asset-form">
                    <div className="form-grid">
                        <div className="form-group">
                            <label htmlFor="expendedAssetId">Asset to Expend:</label>
                            <select
                                id="expendedAssetId"
                                name="expendedAssetId"
                                value={expenditureForm.expendedAssetId}
                                onChange={handleExpenditureChange}
                                required
                            >
                                <option value="">-- Select Asset --</option>
                                {availableAssets.map(asset => (
                                    <option key={asset.id} value={asset.id}>
                                        {asset.name} ({asset.id}) - {asset.status}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label htmlFor="expenditureDate">Expenditure Date:</label>
                            <input
                                type="date"
                                id="expenditureDate"
                                name="expenditureDate"
                                value={expenditureForm.expenditureDate}
                                onChange={handleExpenditureChange}
                                required
                            />
                        </div>
                        <div className="form-group full-width">
                            <label htmlFor="reason">Reason for Expenditure:</label>
                            <textarea
                                id="reason"
                                name="reason"
                                value={expenditureForm.reason}
                                onChange={handleExpenditureChange}
                                placeholder="e.g., Damaged beyond repair, Lost in action, Consumed"
                                rows="3"
                                required
                            ></textarea>
                        </div>
                    </div>
                    <button type="submit" className="submit-button delete-button">Mark Expended</button>
                </form>

                <h3 className="sub-section-title">Expended Assets History</h3>
                {expendedAssetsHistory.length === 0 ? (
                    <p className="empty-state">No assets have been marked as expended.</p>
                ) : (
                    <div className="expenditures-table-container">
                        <table className="data-table">
                            <thead>
                                <tr>
                                    <th>Expenditure ID</th>
                                    <th>Asset ID</th>
                                    <th>Asset Name</th>
                                    <th>Expenditure Date</th>
                                    <th>Reason</th>
                                </tr>
                            </thead>
                            <tbody>
                                {expendedAssetsHistory.map(expenditure => (
                                    <tr key={expenditure._id}>
                                        <td>{expenditure._id}</td>
                                        <td>{expenditure.asset ? expenditure.asset.id : 'N/A'}</td>
                                        <td>{expenditure.asset ? expenditure.asset.name : 'Asset Not Found'}</td>
                                        <td>{new Date(expenditure.expenditureDate).toLocaleDateString()}</td>
                                        <td>{expenditure.reason}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </section>
        </div>
    );
}
