// my-app/src/components/TransfersPage.js
import React, { useState, useEffect } from "react";
import "./TransferPage.css"; // Correct import for its own CSS

const TransferPage = () => {
  const [transferData, setTransferData] = useState({
    fromBase: "",
    toBase: "",
    assetId: "",
    quantity: "",
  });

  const [transferHistory, setTransferHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_BASE_URL = "http://localhost:5000/api/transfers"; // Make sure this matches your backend server URL and route

  // Fetch transfers on component mount
  useEffect(() => {
    const fetchTransfers = async () => {
      try {
        const response = await fetch(API_BASE_URL);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setTransferHistor(data);
      } catch (err) {
        console.error("Error fetching transfers:", err);
        setError("Failed to load transfer history.");
      } finally {
        setLoading(false);
      }
    };

    fetchTransfers();
  }, []); // Empty dependency array means this runs once on mount

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTransferData((prev) => ({ ...prev, [name]: value }));
  };

  const handleTransfer = async (e) => { // Made async
    e.preventDefault();

    if (
      !transferData.fromBase ||
      !transferData.toBase ||
      !transferData.assetId ||
      !transferData.quantity
    ) {
      alert("Please fill in all transfer details.");
      return;
    }

    if (transferData.fromBase === transferData.toBase) {
      alert("From Base and To Base cannot be the same.");
      return;
    }

    try {
      const response = await fetch(API_BASE_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(transferData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
      }

      const newTransfer = await response.json();
      setTransferHistory([newTransfer, ...transferHistory]); // Add new transfer to the top
      setTransferData({ fromBase: "", toBase: "", assetId: "", quantity: "" }); // Clear form fields
      alert("Transfer successful!"); // Provide user feedback
    } catch (err) {
      console.error("Error during transfer:", err);
      alert(`Transfer failed: ${err.message}`); // Show specific error to user
    }
  };

  return (
    <div className="transfer-page">
      <h1 className="title">
        <span role="img" aria-label="truck" className="emoji">🚚</span> Asset Transfer Management
      </h1>

      <section className="transfer-form-section">
        <h2>Initiate Asset Transfer</h2>
        <form onSubmit={handleTransfer} className="transfer-form">
          <div className="form-group">
            <label htmlFor="fromBase">From Base:</label>
            <select
              id="fromBase"
              name="fromBase"
              value={transferData.fromBase}
              onChange={handleChange}
              required
            >
              <option value="">Select Origin Base</option>
              <option value="Base A">Base A</option>
              <option value="Base B">Base B</option>
              <option value="Base C">Base C</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="toBase">To Base:</label>
            <select
              id="toBase"
              name="toBase"
              value={transferData.toBase}
              onChange={handleChange}
              required
            >
              <option value="">Select Destination Base</option>
              <option value="Base A">Base A</option>
              <option value="Base B">Base B</option>
              <option value="Base C">Base C</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="assetId">Asset ID:</label>
            <input
              id="assetId"
              type="text"
              name="assetId"
              value={transferData.assetId}
              onChange={handleChange}
              placeholder="e.g., ASSET-12345"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="quantity">Quantity:</label>
            <input
              id="quantity"
              type="number"
              name="quantity"
              value={transferData.quantity}
              onChange={handleChange}
              placeholder="Enter quantity"
              min="1"
              required
            />
          </div>

          <button type="submit" className="transfer-button">Transfer Asset</button>
        </form>
      </section>

      <section className="history-section">
        <h2><span role="img" aria-label="scroll" className="emoji">📜</span> Transfer History</h2>
        {loading ? (
          <p>Loading transfer history...</p>
        ) : error ? (
          <p className="error-message">{error}</p>
        ) : transferHistory.length === 0 ? (
          <p className="no-transfers">No transfers recorded yet. Start by transferring an asset!</p>
        ) : (
          <ul className="history-list">
            {transferHistory.map((entry) => (
              <li key={entry._id || entry.timestamp} className="history-item"> {/* Use _id if available, fallback to timestamp */}
                <div>
                  <strong>{entry.assetId}</strong> ({entry.quantity} units)
                </div>
                <div className="transfer-details">
                  {entry.fromBase} <span className="arrow">➡️</span> {entry.toBase}
                </div>
                <div className="timestamp">
                  <span role="img" aria-label="clock">⏱</span> {new Date(entry.timestamp).toLocaleString()}
                </div>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default TransferPage;
