import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  FaBoxes,
  FaExchangeAlt
} from 'react-icons/fa';
import MetricCard from './MetricCard';

function Dashboard({ filters, onNetMovementClick }) {
  const navigate = useNavigate();
  const [selectedCard, setSelectedCard] = useState(null);

  // Sample values (you can replace with actual props or state)
  const warehouseStock = 100;
  const returns = 25;
  const assignedAssets = 75;

  const cards = [
    {
      key: "warehouseStock",
      title: "Purchase",
      icon: FaBoxes,
      value: warehouseStock,
      progress: "80%",
      clickable: true,
      onClick: () => navigate("/purchases"),
    },
    {
      key: "returns",
      title: "Transfers",
      icon: FaExchangeAlt,
      value: returns,
      progress: "20%",
      clickable: true,
      onClick: () => navigate("/transfers"),
    },
    {
      key: "assignedAssets",
      title: "Assignments",
      icon: FaBoxes,
      value: assignedAssets,
      progress: "90%",
      clickable: true,
      onClick: () => navigate("/assignments"),
    },
  ];

  return (
    <div className="dashboard">
      <MetricCard title="Opening Balance" value={120} />
      <MetricCard title="Closing Balance" value={180} />
      <MetricCard title="Net Movement" value={60} onClick={onNetMovementClick} clickable />
      <MetricCard title="Assigned Assets" value={40} />
      <MetricCard title="Expended Assets" value={10} />

      {cards.map((card) => (
        <DashboardCard
          key={card.key}
          {...card}
          selected={selectedCard === card.key}
          onClick={() => {
            setSelectedCard(card.key);
            card.onClick();
          }}
        />
      ))}
    </div>
  );
}

export default Dashboard;

// Optional DashboardCard if you're not importing from another file
function DashboardCard({
  title,
  icon: Icon,
  value,
  clickable,
  onClick,
  progress,
  selected,
}) {
  const progressStyle = progress ? { "--progress": progress } : {};

  return (
    <div
      className={`card ${clickable ? "clickable" : ""} ${
        selected ? "selected" : ""
      }`}
      onClick={clickable ? onClick : undefined}
      role={clickable ? "button" : undefined}
      tabIndex={clickable ? 0 : undefined}
      onKeyDown={clickable ? (e) => e.key === "Enter" && onClick() : undefined}
      aria-pressed={selected ? "true" : "false"}
      aria-label={title}
    >
      <div className="card-header">
        <Icon className="icon" />
        <h3>{title}</h3>
      </div>
      <p className="value">{value}</p>
      {progress && (
        <div className="progress-bar">
          <div className="progress-fill" style={progressStyle}></div>
        </div>
      )}
    </div>
  );
}
