// AssignmentsPage.js
import React, { useState } from 'react';
import './AssignmentsPage.css';

const initialAssets = [
    { id: 'AST001', name: 'M16 Rifle', status: 'Available' },
    { id: 'AST002', name: 'Humvee', status: 'Available' },
    { id: 'AST003', name: 'Night Vision Goggles', status: 'Available' },
    { id: 'AST004', name: 'First Aid Kit', status: 'Available' },
];

const dummyPersonnel = [
    { id: 'P001', name: 'John Doe', rank: 'Captain' },
    { id: 'P002', name: 'Jane Smith', rank: 'Sergeant' },
    { id: 'P003', name: 'Mike Johnson', rank: 'Specialist' },
];

export default function AssignmentsPage() {
    const [assets, setAssets] = useState(initialAssets);
    const [assignedAssets, setAssignedAssets] = useState([]);
    const [expendedAssets, setExpendedAssets] = useState([]);

    const [assignmentForm, setAssignmentForm] = useState({
        assetId: '',
        personnelId: '',
        assignmentDate: '',
    });

    const [expenditureForm, setExpenditureForm] = useState({
        expendedAssetId: '',
        expenditureDate: '',
        reason: '',
    });

    const handleAssignmentChange = (e) => {
        const { name, value } = e.target;
        setAssignmentForm(prev => ({ ...prev, [name]: value }));
    };

    const handleExpenditureChange = (e) => {
        const { name, value } = e.target;
        setExpenditureForm(prev => ({ ...prev, [name]: value }));
    };

    const handleAssignAsset = (e) => {
        e.preventDefault();
        const { assetId, personnelId, assignmentDate } = assignmentForm;

        if (!assetId || !personnelId || !assignmentDate) {
            alert('Please fill all assignment fields.');
            return;
        }

        const asset = assets.find(a => a.id === assetId);
        const personnel = dummyPersonnel.find(p => p.id === personnelId);

        if (!asset || asset.status !== 'Available') {
            alert('Asset is not available or not found.');
            return;
        }

        const newAssignment = {
            id: Date.now(),
            asset: { id: asset.id, name: asset.name },
            personnel: { id: personnel.id, name: personnel.name, rank: personnel.rank },
            assignmentDate,
        };

        setAssignedAssets(prev => [...prev, newAssignment]);
        setAssets(prev =>
            prev.map(a =>
                a.id === assetId ? { ...a, status: 'Assigned' } : a
            )
        );
        setAssignmentForm({ assetId: '', personnelId: '', assignmentDate: '' });
        alert('Asset assigned successfully!');
    };

    const handleExpendedAsset = (e) => {
        e.preventDefault();
        const { expendedAssetId, expenditureDate, reason } = expenditureForm;

        if (!expendedAssetId || !expenditureDate || !reason) {
            alert('Please fill all expenditure fields.');
            return;
        }

        const asset = assets.find(a => a.id === expendedAssetId);
        if (!asset || asset.status === 'Expended') {
            alert('Asset not found or already expended.');
            return;
        }

        const newExpenditure = {
            id: Date.now(),
            asset: { id: asset.id, name: asset.name },
            expenditureDate,
            reason,
        };

        setExpendedAssets(prev => [...prev, newExpenditure]);
        setAssets(prev =>
            prev.map(a =>
                a.id === expendedAssetId ? { ...a, status: 'Expended' } : a
            )
        );
        setExpenditureForm({ expendedAssetId: '', expenditureDate: '', reason: '' });
        alert('Asset marked as expended!');
    };

    return (
        <div className="assignments-page">
            <h1 className="page-title">
                <span role="img" aria-label="clipboard">üìù</span> Asset Assignments & Expenditures
            </h1>

            {/* Assignment Section */}
            <section className="assignment-section">
                <h2 className="section-title">üôã‚Äç‚ôÇÔ∏è Assign Asset to Personnel</h2>
                <form onSubmit={handleAssignAsset} className="asset-form">
                    <div className="form-grid">
                        <div className="form-group">
                            <label htmlFor="assignAssetId">Asset:</label>
                            <select
                                id="assignAssetId"
                                name="assetId"
                                value={assignmentForm.assetId}
                                onChange={handleAssignmentChange}
                                required
                            >
                                <option value="">-- Select Asset --</option>
                                {assets.map(asset => (
                                    <option key={asset.id} value={asset.id}>
                                        {asset.name} ({asset.id}) - {asset.status}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label htmlFor="personnelId">Personnel:</label>
                            <select
                                id="personnelId"
                                name="personnelId"
                                value={assignmentForm.personnelId}
                                onChange={handleAssignmentChange}
                                required
                            >
                                <option value="">-- Select Personnel --</option>
                                {dummyPersonnel.map(person => (
                                    <option key={person.id} value={person.id}>
                                        {person.name} ({person.rank})
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label htmlFor="assignmentDate">Assignment Date:</label>
                            <input
                                type="date"
                                id="assignmentDate"
                                name="assignmentDate"
                                value={assignmentForm.assignmentDate}
                                onChange={handleAssignmentChange}
                                required
                            />
                        </div>
                    </div>
                    <button type="submit" className="submit-button">Assign Asset</button>
                </form>

                <h3 className="sub-section-title">Current Assignments</h3>
                {assignedAssets.length === 0 ? (
                    <p className="empty-state">No assets currently assigned.</p>
                ) : (
                    <div className="assignments-table-container">
                        <table className="data-table">
                            <thead>
                                <tr>
                                    <th>Asset ID</th>
                                    <th>Asset Name</th>
                                    <th>Assigned To</th>
                                    <th>Rank</th>
                                    <th>Assignment Date</th>
                                </tr>
                            </thead>
                            <tbody>
                                {assignedAssets.map(assignment => (
                                    <tr key={assignment.id}>
                                        <td>{assignment.asset.id}</td>
                                        <td>{assignment.asset.name}</td>
                                        <td>{assignment.personnel.name}</td>
                                        <td>{assignment.personnel.rank}</td>
                                        <td>{assignment.assignmentDate}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </section>

            {/* Expenditure Section */}
            <section className="expenditure-section">
                <h2 className="section-title">üóëÔ∏è Mark Asset as Expended</h2>
                <form onSubmit={handleExpendedAsset} className="asset-form">
                    <div className="form-grid">
                        <div className="form-group">
                            <label htmlFor="expendedAssetId">Asset to Expend:</label>
                            <select
                                id="expendedAssetId"
                                name="expendedAssetId"
                                value={expenditureForm.expendedAssetId}
                                onChange={handleExpenditureChange}
                                required
                            >
                                <option value="">-- Select Asset --</option>
                                {assets.map(asset => (
                                    <option key={asset.id} value={asset.id}>
                                        {asset.name} ({asset.id}) - {asset.status}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label htmlFor="expenditureDate">Expenditure Date:</label>
                            <input
                                type="date"
                                id="expenditureDate"
                                name="expenditureDate"
                                value={expenditureForm.expenditureDate}
                                onChange={handleExpenditureChange}
                                required
                            />
                        </div>
                        <div className="form-group full-width">
                            <label htmlFor="reason">Reason for Expenditure:</label>
                            <textarea
                                id="reason"
                                name="reason"
                                value={expenditureForm.reason}
                                onChange={handleExpenditureChange}
                                placeholder="e.g., Damaged beyond repair, Lost in action, Consumed"
                                rows="3"
                                required
                            ></textarea>
                        </div>
                    </div>
                    <button type="submit" className="submit-button delete-button">Mark Expended</button>
                </form>

                <h3 className="sub-section-title">Expended Assets History</h3>
                {expendedAssets.length === 0 ? (
                    <p className="empty-state">No assets have been marked as expended.</p>
                ) : (
                    <div className="expenditures-table-container">
                        <table className="data-table">
                            <thead>
                                <tr>
                                    <th>Asset ID</th>
                                    <th>Asset Name</th>
                                    <th>Expenditure Date</th>
                                    <th>Reason</th>
                                </tr>
                            </thead>
                            <tbody>
                                {expendedAssets.map(exp => (
                                    <tr key={exp.id}>
                                        <td>{exp.asset.id}</td>
                                        <td>{exp.asset.name}</td>
                                        <td>{exp.expenditureDate}</td>
                                        <td>{exp.reason}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </section>
        </div>
    );
}
