// src/components/AssignmentsPage.js
import React, { useState, useEffect } from 'react';
import './AssignmentsPage.css'; // Make sure this CSS import is correct

export default function AssignmentsPage() {
    const API_BASE_URL = 'http://localhost:5000/api'; // Ensure this matches your backend port

    // State for assets (fetched from backend)
    const [assets, setAssets] = useState([]);
    // State for personnel (fetched from backend)
    const [personnel, setPersonnel] = useState([]);
    // State for assigned assets (fetched from backend)
    const [assignedAssets, setAssignedAssets] = useState([]);

    const [assignmentForm, setAssignmentForm] = useState({
        assetId: '',       // This will hold the custom ID (e.g., 'AST001')
        personnelId: '',   // This will hold the custom ID (e.g., 'P001')
        assignmentDate: '',
    });

    // --- Fetch Data on Component Mount ---
    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch Assets
                const assetsResponse = await fetch(`${API_BASE_URL}/assets`);
                if (!assetsResponse.ok) throw new Error('Failed to fetch assets');
                const assetsData = await assetsResponse.json();
                setAssets(assetsData);

                // Fetch Personnel
                const personnelResponse = await fetch(`${API_BASE_URL}/personnel`);
                if (!personnelResponse.ok) throw new Error('Failed to fetch personnel');
                const personnelData = await personnelResponse.json();
                setPersonnel(personnelData);

                // Fetch Assignments
                const assignmentsResponse = await fetch(`${API_BASE_URL}/assignments`);
                if (!assignmentsResponse.ok) throw new Error('Failed to fetch assignments');
                const assignmentsData = await assignmentsResponse.json();
                setAssignedAssets(assignmentsData);

            } catch (error) {
                console.error('Error fetching data:', error);
                alert('Failed to load data. Please check the backend server and MongoDB connection.');
            }
        };

        fetchData();
    }, []); // Empty dependency array means this runs once on mount

    const handleAssignmentChange = (e) => {
        const { name, value } = e.target;
        setAssignmentForm(prev => ({ ...prev, [name]: value }));
    };

    // --- Handle Asset Assignment (POST request to backend) ---
    const handleAssignAsset = async (e) => {
        e.preventDefault();
        const { assetId, personnelId, assignmentDate } = assignmentForm;

        if (!assetId || !personnelId || !assignmentDate) {
            alert('Please fill all assignment fields.');
            return;
        }

        try {
            const response = await fetch(`${API_BASE_URL}/assignments`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ assetId, personnelId, assignmentDate }),
            });

            const data = await response.json();

            if (!response.ok) {
                // If response is not OK (e.g., 400, 404, 500)
                throw new Error(data.message || 'Failed to assign asset.');
            }

            // Update local state after successful assignment
            // The backend sends back the fully populated assignment object, use that.
            setAssignedAssets(prev => [...prev, data.assignment]);

            // Update the status of the specific asset in the frontend state
            setAssets(prev =>
                prev.map(a =>
                    a.id === assetId ? { ...a, status: 'Assigned' } : a
                )
            );
            setAssignmentForm({ assetId: '', personnelId: '', assignmentDate: '' });
            alert(data.message); // Show success message from backend

        } catch (error) {
            console.error('Error assigning asset:', error);
            alert(`Error: ${error.message}`); // Show error message to user
        }
    };

    return (
        <div className="assignments-page">
            <h1 className="page-title">
                <span role="img" aria-label="clipboard">üìù</span> Asset Assignments
            </h1>

            <section className="assignment-section">
                <h2 className="section-title">üôã‚Äç‚ôÇÔ∏è Assign Asset to Personnel</h2>
                <form onSubmit={handleAssignAsset} className="asset-form">
                    <div className="form-grid">
                        <div className="form-group">
                            <label htmlFor="assignAssetId">Asset:</label>
                            <select
                                id="assignAssetId"
                                name="assetId"
                                value={assignmentForm.assetId}
                                onChange={handleAssignmentChange}
                                required
                            >
                                <option value="">-- Select Asset --</option>
                                {/* Filter available assets for assignment */}
                                {assets.filter(asset => asset.status === 'Available').map(asset => (
                                    <option key={asset.id} value={asset.id}>
                                        {asset.name} ({asset.id}) - {asset.status}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label htmlFor="personnelId">Personnel:</label>
                            <select
                                id="personnelId"
                                name="personnelId"
                                value={assignmentForm.personnelId}
                                onChange={handleAssignmentChange}
                                required
                            >
                                <option value="">-- Select Personnel --</option>
                                {personnel.map(person => (
                                    <option key={person.id} value={person.id}>
                                        {person.name} ({person.rank})
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label htmlFor="assignmentDate">Assignment Date:</label>
                            <input
                                type="date"
                                id="assignmentDate"
                                name="assignmentDate"
                                value={assignmentForm.assignmentDate}
                                onChange={handleAssignmentChange}
                                required
                            />
                        </div>
                    </div>
                    <button type="submit" className="submit-button">Assign Asset</button>
                </form>

                <h3 className="sub-section-title">Current Assignments</h3>
                {assignedAssets.length === 0 ? (
                    <p className="empty-state">No assets currently assigned.</p>
                ) : (
                    <div className="assignments-table-container">
                        <table className="data-table">
                            <thead>
                                <tr>
                                    <th>Asset ID</th>
                                    <th>Asset Name</th>
                                    <th>Assigned To</th>
                                    <th>Rank</th>
                                    <th>Assignment Date</th>
                                </tr>
                            </thead>
                            <tbody>
                                {assignedAssets.map(assignment => (
                                    <tr key={assignment._id}> {/* Use MongoDB's _id for the key */}
                                        <td>{assignment.asset.id}</td>
                                        <td>{assignment.asset.name}</td>
                                        <td>{assignment.personnel.name}</td>
                                        <td>{assignment.personnel.rank}</td>
                                        <td>{new Date(assignment.assignmentDate).toLocaleDateString()}</td> {/* Format date for display */}
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </section>
        </div>
    );
}
