import React, { useState } from 'react';

function Filters({ onFilterChange }) {
  const [date, setDate] = useState('');
  const [base, setBase] = useState('');
  const [equipment, setEquipment] = useState('');

  const handleChange = () => {
    onFilterChange({ date, base, equipment });
  };

  return (
    <div className="filters">
      <input type="date" value={date} onChange={(e) => { setDate(e.target.value); handleChange(); }} />
      <input type="text" placeholder="Base" value={base} onChange={(e) => { setBase(e.target.value); handleChange(); }} />
      <input type="text" placeholder="Equipment Type" value={equipment} onChange={(e) => { setEquipment(e.target.value); handleChange(); }} />
    </div>
  );
}

export default Filters;
