// TransferPage.jsx
import React, { useState } from "react";
import "./TransferPage.css";

const TransferPage = () => {
  const [transferData, setTransferData] = useState({
    fromBase: "",
    toBase: "",
    assetId: "",
    quantity: "",
  });

  const [transferHistory, setTransferHistory] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTransferData((prev) => ({ ...prev, [name]: value }));
  };

  const handleTransfer = () => {
    if (!transferData.fromBase || !transferData.toBase || !transferData.assetId || !transferData.quantity) return;
    const newTransfer = {
      ...transferData,
      timestamp: new Date().toLocaleString(),
    };
    setTransferHistory([newTransfer, ...transferHistory]);
    setTransferData({ fromBase: "", toBase: "", assetId: "", quantity: "" });
  };

  return (
    <div className="transfer-page">
      <h1 className="title">🚚 Asset Transfer Management</h1>
      <div className="transfer-form">
        <select name="fromBase" value={transferData.fromBase} onChange={handleChange}>
          <option value="">From Base</option>
          <option value="Base A">Base A</option>
          <option value="Base B">Base B</option>
          <option value="Base C">Base C</option>
        </select>

        <select name="toBase" value={transferData.toBase} onChange={handleChange}>
          <option value="">To Base</option>
          <option value="Base A">Base A</option>
          <option value="Base B">Base B</option>
          <option value="Base C">Base C</option>
        </select>

        <input
          type="text"
          name="assetId"
          value={transferData.assetId}
          onChange={handleChange}
          placeholder="Asset ID"
        />

        <input
          type="number"
          name="quantity"
          value={transferData.quantity}
          onChange={handleChange}
          placeholder="Quantity"
        />

        <button onClick={handleTransfer}>Transfer Asset</button>
      </div>

      <div className="history">
        <h2>📜 Transfer History</h2>
        {transferHistory.length === 0 ? (
          <p>No transfers recorded yet.</p>
        ) : (
          <ul>
            {transferHistory.map((entry, index) => (
              <li key={index} className="history-item">
                <div><strong>{entry.assetId}</strong> ({entry.quantity} units)</div>
                <div>{entry.fromBase} ➡️ {entry.toBase}</div>
                <div className="timestamp">⏱ {entry.timestamp}</div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default TransferPage;
